import { useEffect, useState } from "react"
import { Link } from "react-router-dom"
import Bookmark from './../pages/Bookmark'
import ProductList from './../pages/ProductList'

import { data } from './../data/data.js'

export default function Main() {
    const [isProduct, setProduct] = useState(data)
    const [isBookmark, setBookmark] = useState(data)

    return (
        <div className="main">
            <div className="container">
                <Link to="/product"><button>상품리스트</button></Link>
                <ProductList isProduct={isProduct} />
                <Link to="/bookmark"><button>북마크리스트</button></Link>
                <Bookmark isBookmark={isBookmark} />
            </div>
        </div>
    )
}
